// Modal.js
import React from 'react';
import './Modal.css';  // To style the modal

const Modal = ({ showModal, handleClose, report }) => {
  if (!showModal) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={handleClose}>X</button>
        <h2>{report.name}'s Numerology Report</h2>
        <p><strong>Life Path Number:</strong> {report.lifePathNumber}</p>
        <p><strong>Expression Number:</strong> {report.expressionNumber}</p>
        <p><strong>Soul Urge Number:</strong> {report.soulUrgeNumber}</p>
        <p><strong>Personality Number:</strong> {report.personalityNumber}</p>
      </div>
    </div>
  );
};

export default Modal;
