import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [name, setName] = useState('');
  const [dob, setDob] = useState('');
  const [reports, setReports] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !dob) return alert("Please enter both fields");

    try {
      const res = await axios.post('http://localhost:3002/api/fetchreports', { name, dob });
      alert(`Report Generated:\nLife Path Number: ${res.data.report.lifePathNumber}\nExpression Number: ${res.data.report.expressionNumber}\nSoul Urge Number: ${res.data.report.soulUrgeNumber}\nPersonality Number: ${res.data.report.personalityNumber}`);
      fetchReports();
    } catch (error) {
      alert('Error generating report');
    }
  };

  const fetchReports = async () => {
    try {
      const res = await axios.get('http://localhost:3002/api/fetchreports');
      setReports(res.data);
    } catch (error) {
      alert('Error fetching reports');
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif' }}>
      <h1>Numerology Prediction</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
        <input type="date" value={dob} onChange={(e) => setDob(e.target.value)} required />
        <button type="submit">Get Report</button>
      </form>

      <h2>Past Reports</h2>
      <table border="1" cellPadding="10">
        <thead>
          <tr><th>Name</th><th>DOB</th><th>Life Path</th><th>Expression</th><th>Soul Urge</th><th>Personality</th></tr>
        </thead>
        <tbody>
          {reports.map((r, i) => (
            <tr key={i}>
              <td>{r.name}</td>
              <td>{r.dob}</td>
              <td>{r.lifePathNumber}</td>
              <td>{r.expressionNumber}</td>
              <td>{r.soulUrgeNumber}</td>
              <td>{r.personalityNumber}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
